import React, { useEffect } from 'react'
import { Sidebar, Menu, MenuItem } from 'react-pro-sidebar';
import { useLocation } from 'react-router-dom';

const Sidebars = () => {
    const loc = useLocation()

    const [active, setActive] = useState('/user/dashboard')

    useEffect(() => {
        switch (loc.pathname) {
            case '/user/dashboard':
                setActive('Home')
                break;
            case '/user/add-contacts':
                setActive('Add')
                break;
            case '/user/show-contacts':
                setActive('Show')
                break;
            case '/user/profile':
                setActive('Profile')
                break;
            case '/user/settings':
                setActive('Settings')
                break;
            default:
                break;
        }
    }, [loc])

    return (
        <Sidebar>
            <Menu>
                <MenuItem active={active === 'Home'}> Home </MenuItem>
                <MenuItem active={active === 'Add'}> Add Contacts </MenuItem>
                <MenuItem active={active === 'Show'}> View Contacts </MenuItem>
                <MenuItem active={active === 'Profile'}> Profile </MenuItem>
                <MenuItem active={active === 'Home'}> Settings </MenuItem>
            </Menu>
        </Sidebar>
    )
}

export default Sidebars
